package assignment1;


public class SwapingOfVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		int x,y,t;
		x=9;
		y=18;
		
		System.out.println("befor swaping: "+x+" "+y);
		
		t=x;
		x=y;
		y=t;
		
		System.out.println("After swaping: "+x+" "+y);
		
		

	}

}
